import{_ as e,j as o,g as t,G as i}from"./chunks/framework.791f7e63.js";const p='{"title":"Migration from VitePress 0.x","description":"","frontmatter":{},"headers":[{"level":2,"title":"App Config","slug":"app-config"},{"level":2,"title":"Theme Config","slug":"theme-config"},{"level":2,"title":"Frontmatter Config","slug":"frontmatter-config"}],"relativePath":"guide/migration-from-vitepress-0.md","lastUpdated":1654442365000}',a={name:"guide/migration-from-vitepress-0.md"},n=i('<h1 id="migration-from-vitepress-0-x" tabindex="-1">Migration from VitePress 0.x <a class="header-anchor" href="#migration-from-vitepress-0-x" aria-hidden="true">#</a></h1><p>If you&#39;re coming from VitePress 0.x version, there&#39;re several breaking changes due to new features and enhancement. Please follow this guide to see how to migrate your app over to the latest VitePress.</p><h2 id="app-config" tabindex="-1">App Config <a class="header-anchor" href="#app-config" aria-hidden="true">#</a></h2><ul><li>The internationalization feature is not yet implemented.</li></ul><h2 id="theme-config" tabindex="-1">Theme Config <a class="header-anchor" href="#theme-config" aria-hidden="true">#</a></h2><ul><li><code>sidebar</code> option has changed its structure. <ul><li><code>children</code> key is now named <code>items</code>.</li><li>Top level item may not contain <code>link</code> at the moment. We&#39;re planning to bring it back.</li></ul></li><li><code>repo</code>, <code>repoLabel</code>, <code>docsDir</code>, <code>docsBranch</code>, <code>editLinks</code>, <code>editLinkText</code> are removed in favor of more flexible api. <ul><li>For adding GitHub link with icon to the nav, use <a href="./theme-nav.html#navigation-links">Social Links</a> feature.</li><li>For adding &quot;Edit this page&quot; feature, use <a href="./theme-edit-link.html">Edit Link</a> feature.</li></ul></li><li><code>lastUpdated</code> option is now split into <code>config.lastUpdated</code> and <code>themeConfig.lastUpdatedText</code>.</li><li><code>carbonAds.carbon</code> is changed to <code>carbonAds.code</code>.</li></ul><h2 id="frontmatter-config" tabindex="-1">Frontmatter Config <a class="header-anchor" href="#frontmatter-config" aria-hidden="true">#</a></h2><ul><li><code>home: true</code> option has changed to <code>layout: home</code>. Also, many Homepage related settings have been modified to provide additional features. See <a href="./theme-homepage.html">Homepage guide</a> for details.</li><li><code>footer</code> option is moved to <a href="./../config/theme-configs.html#footer"><code>themeConfig.footer</code></a>.</li></ul>',8),r=[n];function d(c,l,s,h,m,f){return t(),o("div",null,r)}var u=e(a,[["render",d]]);export{p as __pageData,u as default};
